import axios from 'axios';

// Pastikan URL sudah benar
const services = {
  hotel: 'http://localhost:5001',
  auth: 'http://localhost:5002',
  reservation: 'http://localhost:5003',
  notification: 'http://localhost:5004'
};

// Membuat instance axios untuk setiap service
const hotelAPI = axios.create({ baseURL: services.hotel });
const authAPI = axios.create({ baseURL: services.auth });
const reservationAPI = axios.create({ baseURL: services.reservation });
const notificationAPI = axios.create({ baseURL: services.notification });

// API endpoints
export const api = {
  // Auth Service
  login: (credentials) => authAPI.post('/api/auth/login', credentials),
  logout: () => authAPI.post('/api/auth/logout'),

  // Hotel Service
  getRooms: () => hotelAPI.get('/api/rooms'),
  getRoom: (id) => hotelAPI.get(`/api/rooms/${id}`),
  createRoom: (data) => hotelAPI.post('/api/rooms', data),
  updateRoom: (id, data) => hotelAPI.put(`/api/rooms/${id}`, data),
  deleteRoom: (id) => hotelAPI.delete(`/api/rooms/${id}`),

  // Reservation Service
  createBooking: (data) => reservationAPI.post('/api/bookings', data),
  getBookings: () => reservationAPI.get('/api/bookings'),
  updateBooking: (id, data) => reservationAPI.put(`/api/bookings/${id}`, data),

  // Notification Service
  sendNotification: (data) => notificationAPI.post('/api/notifications', data)
};

export default api;
