import React from 'react';
import { Box, Container, Typography, Button, Grid, Card, CardContent, CardMedia } from '@mui/material';
import { Link } from 'react-router-dom';

function SearchRoom() {
  const rooms = [
    {
      id: 1,
      type: "Standard Room",
      price: "500.000",
      image: "https://via.placeholder.com/300x200",
      description: "Kamar standar dengan fasilitas dasar yang nyaman"
    },
    {
      id: 2,
      type: "Deluxe Room",
      price: "800.000",
      image: "https://via.placeholder.com/300x200",
      description: "Kamar deluxe dengan tambahan fasilitas premium"
    }
  ];

  return (
    <Container>
      <Box sx={{ py: 4 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 4 }}>
          <Typography variant="h4">Cari Kamar Hotel</Typography>
          <Button 
            component={Link} 
            to="/admin/login"
            variant="contained"
            color="primary"
          >
            Admin Login
          </Button>
        </Box>
        
        <Grid container spacing={3}>
          {rooms.map((room) => (
            <Grid item xs={12} sm={6} md={4} key={room.id}>
              <Card>
                <CardMedia
                  component="img"
                  height="200"
                  image={room.image}
                  alt={room.type}
                />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="div">
                    {room.type}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {room.description}
                  </Typography>
                  <Typography variant="h6" color="primary" sx={{ mt: 2 }}>
                    Rp {room.price}/malam
                  </Typography>
                  <Button 
                    component={Link}
                    to={`/booking/${room.id}`}
                    variant="contained" 
                    fullWidth 
                    sx={{ mt: 2 }}
                  >
                    Booking
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Container>
  );
}

export default SearchRoom;
