import React, { useState, useEffect } from 'react';
import { Box, Container, Typography, Button, Grid, Card, CardContent, CardMedia, CircularProgress, Alert } from '@mui/material';
import { Link } from 'react-router-dom';
import api from '../../services/api';

function SearchRoom() {
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchRooms();
  }, []);

  const fetchRooms = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await api.getRooms();
      setRooms(response.data);
    } catch (error) {
      setError('Gagal mengambil data kamar: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        mb: 6,
        px: 2 
      }}>
        <Typography variant="h4" component="h1" sx={{ fontWeight: 'bold' }}>
          Cari Kamar Hotel
        </Typography>
        <Button 
          component={Link} 
          to="/admin/login"
          variant="contained"
          sx={{
            backgroundColor: '#1976d2',
            '&:hover': {
              backgroundColor: '#1565c0'
            }
          }}
        >
          ADMIN LOGIN
        </Button>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 4 }}>
          {error}
        </Alert>
      )}
      
      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
          <CircularProgress />
        </Box>
      ) : (
        <Grid container spacing={4} sx={{ px: 2 }}>
          {rooms.map((room) => (
            <Grid item xs={12} sm={6} key={room.id}>
              <Card sx={{ 
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                boxShadow: '0 4px 8px rgba(0,0,0,0.1)'
              }}>
                <CardMedia
                  component="img"
                  height="250"
                  image={room.image || "https://via.placeholder.com/300x200"}
                  alt={room.room_type}
                  sx={{ objectFit: 'cover' }}
                />
                <CardContent sx={{ flexGrow: 1, p: 3 }}>
                  <Typography gutterBottom variant="h5" component="h2" sx={{ fontWeight: 'bold' }}>
                    {room.room_type}
                  </Typography>
                  <Typography variant="body1" color="text.secondary" sx={{ mb: 2 }}>
                    {room.description}
                  </Typography>
                  <Typography variant="h6" color="primary" sx={{ mb: 2, fontWeight: 'bold' }}>
                    Rp {room.price}/malam
                  </Typography>
                  <Button 
                    component={Link}
                    to={`/booking/${room.id}`}
                    variant="contained"
                    fullWidth
                    disabled={!room.available}
                    sx={{
                      mt: 'auto',
                      py: 1.5,
                      backgroundColor: '#1976d2',
                      '&:hover': {
                        backgroundColor: '#1565c0'
                      }
                    }}
                  >
                    {room.available ? 'BOOKING' : 'TIDAK TERSEDIA'}
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}
    </Container>
  );
}

export default SearchRoom;
